        ])
        form.beforeSubmit = function () {
            if (getCookie($('#subscribeForm input').val())) {
                $('#subscribeForm').append('<p class="error-alert">This email is already in use.</p>');
                return false;
            } else {
                writeCookie($('#subscribeForm input').val(), 'true', 30);
                return true;
            }
        }
        form.afterSubmit = function (response) {
            $('#subscribeForm input').fadeOut(150);
            $('.footer-top__subscribe p').html('THE FIRST DISCOUNT IS ALREADY IN YOUR INBOX');
            $('.footer-top__subscribe button').css('border-radius', '3px');
            $('.footer-top__subscribe button').html('USE IT NOW');
            $('.footer-top__subscribe button').on('click', function () {
                window.location = '/order/?coupon=GET10OFF'
            });
            writeCookie("footer_getessay_hide", true, 30);
        };
    });
